VALUES
	(-1090511, 'Minions! Take my brother and his drake to their new dwellings. Make sure the beast makes it alive. I have special plans for the broodmother.', NULL, NULL, NULL, NULL, NULL, '¡Esbirros! Llevad a mi hermano y a su draco a sus nuevos aposentos. Aseguraos de que la bestia siga con vida. Tengo planes especiales para la madre de linaje.', '¡Esbirros! Llevad a mi hermano y a su draco a sus nuevos aposentos. Aseguraos de que la bestia siga con vida. Tengo planes especiales para la madre de linaje.', NULL, 0, 0, 0, 0, 'EVENT_12_LOKEN');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`)
VALUES
	(-1090512, 'As for your life, mortal. I will be generous. After all... why would I destroy my most useful servant? I waited for you for weeks inside that Hyldnir mine.', NULL, NULL, NULL, NULL, NULL, 'Respecto a tu vida, mortal, seré generoso. Después de todo... ¿por qué iba a destruir a mi más útil sirviente? Llevo semanas esperándote dentro de esa mina Hyldnir.', 'Respecto a tu vida, mortal, seré generoso. Después de todo... ¿por qué iba a destruir a mi más útil sirviente? Llevo semanas esperándote dentro de esa mina Hyldnir.', NULL, 0, 0, 0, 0, 'EVENT_13_LOKEN');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`)
VALUES
	(-1090513, 'The shape wasn\'t mine, of course. And had you not been so reckless, you would\'ve seen past my illusion. But you came through for me, and for that... I must thank you!', NULL, NULL, NULL, NULL, NULL, 'La forma no era mía, por supuesto. Y si no hubieras sido tan temerario, habrías visto más allá de mi ilusión. Pero viniste a por mí, y por eso debo darte las gracias.', 'La forma no era mía, por supuesto. Y si no hubieras sido tan temerario, habrías visto más allá de mi ilusión. Pero viniste a por mí, y por eso debo darte las gracias.', NULL, 0, 0, 0, 0, 'EVENT_14_LOKEN');
