UPDATE `script_texts` SET `content_loc7`='Preparaos, heroes, para los temibles gemelos: ¡Fauceacida y Aterraescama! ¡A la arena!' WHERE `content_default`='Steel yourselves, heroes, for the twin terrors Acidmaw and Dreadscale. Enter the arena!';
UPDATE `script_texts` SET `content_loc7`='El propio aire se congela al presentar a nuestro siguiente combatiente: ¡Aullahielo! ¡Matad o morid, campeones!' WHERE `content_default`='The air freezes with the introduction of our next combatant, Icehowl! Kill or be killed, champions!';
