UPDATE `script_texts` SET `content_loc7`='Tragico... Lucharon con valor, pero las bestias de Rasganorte triunfaron. Guardemos un minuto de silencio por nuestros heroes caidos.' WHERE `content_default`='Tragic... They fought valiantly, but the beasts of Northrend triumphed. Let us observe a moment of silence for our fallen heroes.';
UPDATE `script_texts` SET `content_loc7`='El gran brujo Wilfred Chispobang invocara al siguiente contrincante. Esperad a que aparezca.' WHERE `content_default`='Grand Warlock Wilfred Fizzlebang will summon forth your next challenge. Stand by for his entry!';
UPDATE `script_texts` SET `content_loc7`='Gracias, Alto Señor. Ahora, competidores, comenzare el ritual de invocacion. ¡Cuando termine, aparecera un temible guardia apocaliptico!' WHERE `content_default`='Thank you, Highlord! Now challengers, I will begin the ritual of summoning! When I am done, a fearsome Doomguard will appear!';
UPDATE `script_texts` SET `content_loc7`='¡Preparaos para la agonia!' WHERE `content_default`='Prepare for oblivion!';
UPDATE `script_texts` SET `content_loc7`='¡AJa! ¡Lo he logrado! ¡Observad el poder absoluto de Wilfred Chispobang, maestro invocador! ¡Debes obedecerme, demonio!' WHERE `content_default`='Ah ha! Behold the absolute power of Wilfred Fizzlebang, master summoner! You are bound to ME, demon!';
